 91	// Statistics helper functions
 92	export const calculateAttendanceStats = (records: AttendanceRecord[]) => {
 93	 const total = records.length;
 94	 const present = records.filter(r => r.status === 'Hadir').length;
 95	 const late = records.filter(r => r.status === 'Terlambat').length;
 96	 const absent = records.filter(r => r.status === 'Tidak Hadir').length;
 97	 const excused = records.filter(r => r.status === 'Izin').length;
 98	 
 99	 const attendanceRate = total > 0 ? ((present + late) / total) * 100 : 0;
 100	 
 101	 return {
 102	 total,
 103	 present,
 104	 late,
 105	 absent,
 106	 excused,
 107	 attendanceRate: Math.round(attendanceRate * 100) / 100
 108	 };
 109	};
