 63	export const getAttendanceByDate = (date: string): AttendanceRecord[] => {
 64	 return attendanceData.filter(record => record.date === date);
 65	};
 66	
 67	export const getAttendanceByClass = (className: string): AttendanceRecord[] => {
 68	 return attendanceData.filter(record => record.className === className);
 69	};
 70	
 71	export const getAttendanceByClassAndDate = (className: string, date: string): AttendanceRecord[] => {
 72	 return attendanceData.filter(record => record.className === className && record.date === date);
 73	};
 74	
 75	export const getAttendanceByDateRange = (startDate: string, endDate: string): AttendanceRecord[] => {
 76	 return attendanceData.filter(record => record.date >= startDate && record.date <= endDate);
 77	};
 78	
 79	export const getAttendanceByStudent = (studentId: string): AttendanceRecord[] => {
 80	 return attendanceData.filter(record => record.studentId === studentId);
 81	};
 82	
 83	export const getAttendanceByClassAndDateRange = (className: string, startDate: string, endDate: string): AttendanceRecord[] => {
 84	 return attendanceData.filter(record => 
 85	 record.className === className && 
 86	 record.date >= startDate && 
 87	 record.date <= endDate
 88	 );
 89	};
 90	
 91	// Statistics helper functions
 92	export const calculateAttendanceStats = (records: AttendanceRecord[]) => {
 93	 const total = records.length;
 94	 const present = records.filter(r => r.status === 'Hadir').length;
 95	 const late = records.filter(r => r.status === 'Terlambat').length;
 96	 const absent = records.filter(r => r.status === 'Tidak Hadir').length;
 97	 const excused = records.filter(r => r.status === 'Izin').length;
 98	 
 99	 const attendanceRate = total > 0 ? ((present + late) / total) * 100 : 0;
 100	 
 101	 return {
 102	 total,
 103	 present,
 104	 late,
 105	 absent,
 106	 excused,
 107	 attendanceRate: Math.round(attendanceRate * 100) / 100
 108	 };
 109	};
 110	
 111	// Get unique dates from attendance data
 112	export const getAvailableDates = (): string[] => {
 113	 const dates = [...new Set(attendanceData.map(record => record.date))];
 114	 return dates.sort();
 115	};
 116	
