Path: src/data/attendanceData.ts
...
 119	
 120	// Statistics helper functions
 121	export const calculateAttendanceStats = (records: AttendanceRecord[]) => {
 122	 const total = records.length;
 123	 const present = records.filter(r => r.status === 'Hadir').length;
 124	 const late = records.filter(r => r.status === 'Terlambat').length;
 125	 const absent = records.filter(r => r.status === 'Tidak Hadir').length;
 126	 const excused = records.filter(r => r.status === 'Izin').length;
 127	
 128	 const attendanceRate = total > 0 ? ((present + late) / total) * 100 : 0;
 129	
 130	 return {
 131	 total,
 132	 present,
 133	 late,
 134	 absent,
 135	 excused,
 136	 attendanceRate: Math.round(attendanceRate * 100) / 100
 137	 };
 138	};
