...
 27	
 28	 useEffect(() => {
 29	 // Check if user is logged in on app start
 30	 const checkAuth = () => {
 31	 try {
 32	 const storedIsLoggedIn = localStorage.getItem('isLoggedIn');
 33	 const storedUser = localStorage.getItem('user');
 34	
 35	 if (storedIsLoggedIn === 'true' && storedUser) {
 36	 const parsedUser = JSON.parse(storedUser);
 37	 setUser(parsedUser);
 38	
 39	 if (isTeacher(parsedUser)) {
 40	 setTeacher(parsedUser);
 41	 setAdmin(null);
 42	 } else if (isAdmin(parsedUser)) {
 43	 setAdmin(parsedUser);
 44	 setTeacher(null);
 45	 }
 46	
 47	 setIsLoggedIn(true);
 48	 }
 49	 } catch (error) {
 50	 console.error('Error checking auth:', error);
 51	 // Clear invalid data
 52	 localStorage.removeItem('isLoggedIn');
 53	 localStorage.removeItem('user');
 54	 localStorage.removeItem('teacher'); // Legacy cleanup
 55	 } finally {
 56	 setIsLoading(false);
 57	 }
 58	 };
 59	
 60	 checkAuth();
 61	 }, []);
 62	
 63	 const login = (username: string, password: string): boolean => {
 64	 const userData = validateUserCredentials(username, password);
 65	
 66	 if (userData) {
 67	 setUser(userData);
 68	
 69	 if (isTeacher(userData)) {
 70	 setTeacher(userData);
 71	 setAdmin(null);
 72	 } else if (isAdmin(userData)) {
 73	 setAdmin(userData);
 74	 setTeacher(null);
 75	 }
 76	
 77	 setIsLoggedIn(true);
 78	 localStorage.setItem('isLoggedIn', 'true');
 79	 localStorage.setItem('user', JSON.stringify(userData));
 80	
 81	 return true;
 82	 }
 83	
 84	 return false;
 85	 };
 86	
 87	 const logout = () => {
 88	 setUser(null);
 89	 setTeacher(null);
 90	 setAdmin(null);
 91	 setIsLoggedIn(false);
 92	 localStorage.removeItem('isLoggedIn');
 93	 localStorage.removeItem('user');
 94	 localStorage.removeItem('teacher'); // Legacy cleanup
 95	 };
 96	
 97	 const value = {
 98	 user,
 99	 teacher,
 100	 admin,
 101	 isLoggedIn,
 102	 isLoading,
 103	 login,
 104	 logout,
 105	 isAuthenticated: isLoggedIn,
 106	 hasAdminAccess: admin !== null,
 107	 hasTeacherAccess: teacher !== null,
 108	 };
 109	
 110	 return (
 111	 <AuthContext.Provider value={value}>
 112	 {children}
 113	 </AuthContext.Provider>
 114	 );
 115	}
