 4	import attendanceDataJSON from './attendanceData.json';
 5	
 6	export type AttendanceStatus = 'Hadir' | 'Terlambat' | 'Tidak Hadir' | 'Izin';
 7	
 8	export interface AttendanceRecord {
 9	 id: string;
 10	 studentId: string;
 11	 studentName: string;
 12	 className: string;
 13	 date: string; // YYYY-MM-DD format
 14	 status: AttendanceStatus;
 15	 timeIn?: string; // HH:MM format for present/late students
 16	 notes?: string; // Optional notes for absences/excuses
 17	 recordedBy: string; // Teacher/Admin who recorded
 18	 recordedAt: string; // Timestamp when recorded
 19	}
 20	
 21	export interface AttendanceDataStructure {
 22	 metadata: {
 23	 generatedAt: string;
 24	 totalRecords: number;
 25	 dateRange: {
 26	 start: string;
 27	 end: string;
 28	 };
 29	 schoolDays: number;
 30	 classes: string[];
 31	 studentsPerClass: number;
 32	 totalStudents: number;
 33	 attendancePatterns: {
 34	 present: number;
 35	 late: number;
 36	 absent: number;
 37	 excused: number;
 38	 };
 39	 };
 40	 students: Array<{
 41	 id: string;
 42	 name: string;
 43	 className: string;
 44	 }>;
 45	 teachers: Record<string, string>;
 46	 excuseReasons: string[];
 47	 attendanceRecords: AttendanceRecord[];
 48	}
