 25	export default function LaporanAbsenPage() {
 26	 const { user, teacher, admin, hasAdminAccess, hasTeacherAccess } = useAuth();
 27	 const router = useRouter();
 28	 const [loading, setLoading] = useState(true);
 29	 const [selectedClass, setSelectedClass] = useState<string>('all');
 30	 const [selectedDateRange, setSelectedDateRange] = useState<string>('today');
 31	 const [startDate, setStartDate] = useState<string>('');
 32	 const [endDate, setEndDate] = useState<string>('');
 33	 const [reports, setReports] = useState<AttendanceReport[]>([]);
 34	
 35	 // Check authentication
 36	 useEffect(() => {
 37	 if (!user) {
 38	 router.push('/login');
 39	 return;
 40	 }
 41	 
 42	 // Set default class for teachers
 43	 if (hasTeacherAccess && teacher?.className) {
 44	 setSelectedClass(teacher.className);
 45	 }
 46	 
 47	 // Set default date to latest available date from attendance data
 48	 const availableDates = getAvailableDates();
 49	 const latestDate = availableDates.length > 0 ? availableDates[availableDates.length - 1] : new Date().toISOString().split('T')[0];
 50	 setStartDate(latestDate);
 51	 setEndDate(latestDate);
 52	 
 53	 setLoading(false);
 54	 }, [user, teacher, hasTeacherAccess, router]);
 55	
 56	 // Generate attendance reports
 57	 useEffect(() => {
 58	 if (!loading) {
 59	 generateReports();
 60	 }
 61	 }, [selectedClass, selectedDateRange, startDate, endDate, loading]);
 62	
 63	 const generateReports = () => {
 64	 const classes = getAllClasses();
 65	
 66	 // Filter classes based on selection and user role
 67	 let targetClasses = classes;
 68	 if (hasTeacherAccess && teacher?.className) {
 69	 targetClasses = classes.filter(cls => cls.name === teacher.className);
 70	 } else if (selectedClass !== 'all') {
 71	 targetClasses = classes.filter(cls => cls.name === selectedClass);
 72	 }
 73	
 74	 // Determine date range based on selection
 75	 let reportStartDate = startDate;
 76	 let reportEndDate = endDate;
 77	
 78	 if (selectedDateRange === 'today') {
 79	 const today = new Date().toISOString().split('T')[0];
 80	 reportStartDate = today;
 81	 reportEndDate = today;
 82	 } else if (selectedDateRange === 'week') {
 83	 const today = new Date();
 84	 const weekStart = new Date(today.setDate(today.getDate() - today.getDay() + 1));
 85	 const weekEnd = new Date(today.setDate(today.getDate() - today.getDay() + 5));
 86	 reportStartDate = weekStart.toISOString().split('T')[0];
 87	 reportEndDate = weekEnd.toISOString().split('T')[0];
 88	 } else if (selectedDateRange === 'month') {
 89	 const today = new Date();
 90	 const monthStart = new Date(today.getFullYear(), today.getMonth(), 1);
 91	 const monthEnd = new Date(today.getFullYear(), today.getMonth() + 1, 0);
 92	 reportStartDate = monthStart.toISOString().split('T')[0];
 93	 reportEndDate = monthEnd.toISOString().split('T')[0];
 94	 }
 95	
 96	 // Generate reports for each class using centralized attendance data
 97	 const generatedReports: AttendanceReport[] = targetClasses.map(cls => {
 98	 // Get attendance data for this class and date range
 99	 const attendanceRecords = getAttendanceByClassAndDateRange(cls.name, reportStartDate, reportEndDate);
 100	
 101	 // Calculate statistics using the helper function
 102	 const stats = calculateAttendanceStats(attendanceRecords);
 103	
 104	 return {
 105	 date: reportStartDate === reportEndDate ? reportStartDate : `${reportStartDate} - ${reportEndDate}`,
 106	 className: cls.name,
 107	 totalStudents: stats.total,
 108	 present: stats.present,
 109	 late: stats.late,
 110	 absent: stats.absent,
 111	 excused: stats.excused,
 112	 attendanceRate: stats.attendanceRate
 113	 };
 114	 });
