 66	
 67	export const getAttendanceByClass = (className: string): AttendanceRecord[] => {
 68	 return attendanceData.filter(record => record.className === className);
 69	};
 70	
 71	export const getAttendanceByClassAndDate = (className: string, date: string): AttendanceRecord[] => {
 72	 return attendanceData.filter(record => record.className === className && record.date === date);
 73	};
 74	
 75	export const getAttendanceByDateRange = (startDate: string, endDate: string): AttendanceRecord[] => {
 76	 return attendanceData.filter(record => record.date >= startDate && record.date <= endDate);
 77	};
 78	
 79	export const getAttendanceByStudent = (studentId: string): AttendanceRecord[] => {
 80	 return attendanceData.filter(record => record.studentId === studentId);
 81	};
 82	
 83	export const getAttendanceByClassAndDateRange = (className: string, startDate: string, endDate: string): AttendanceRecord[] => {
 84	 return attendanceData.filter(record =>
 85	 record.className === className &&
 86	 record.date >= startDate &&
 87	 record.date <= endDate
 88	 );
 89	};
 90	
 91	// Get attendance data for the last N days
 92	export const getAttendanceForLastDays = (days: number = 30): AttendanceRecord[] => {
 93	 const endDate = new Date();
 94	 const startDate = new Date();
 95	 startDate.setDate(endDate.getDate() - days);
 96	
 97	 const startDateStr = startDate.toISOString().split('T')[0];
 98	 const endDateStr = endDate.toISOString().split('T')[0];
 99	
 100	 return getAttendanceByDateRange(startDateStr, endDateStr);
 101	};
 102	
 103	// Get attendance data for the last 30 days (convenience function)
 104	export const getAttendanceForLast30Days = (): AttendanceRecord[] => {
 105	 return getAttendanceForLastDays(30);
 106	};
 107	
 108	// Get attendance data for the last N days by class
 109	export const getAttendanceForLastDaysByClass = (className: string, days: number = 30): AttendanceRecord[] => {
 110	 const endDate = new Date();
 111	 const startDate = new Date();
 112	 startDate.setDate(endDate.getDate() - days);
 113	
 114	 const startDateStr = startDate.toISOString().split('T')[0];
 115	 const endDateStr = endDate.toISOString().split('T')[0];
 116	
 117	 return getAttendanceByClassAndDateRange(className, startDateStr, endDateStr);
 118	};
 119	
 120	// Statistics helper functions
 121	export const calculateAttendanceStats = (records: AttendanceRecord[]) => {
 122	 const total = records.length;
 123	 const present = records.filter(r => r.status === 'Hadir').length;
 124	 const late = records.filter(r => r.status === 'Terlambat').length;
 125	 const absent = records.filter(r => r.status === 'Tidak Hadir').length;
 126	 const excused = records.filter(r => r.status === 'Izin').length;
 127	 
 128	 const attendanceRate = total > 0 ? ((present + late) / total) * 100 : 0;
 129	 
 130	 return {
 131	 total,
 132	 present,
 133	 late,
 134	 absent,
 135	 excused,
 136	 attendanceRate: Math.round(attendanceRate * 100) / 100
 137	 };
 138	};
 139	
 140	// Get unique dates from attendance data
 141	export const getAvailableDates = (): string[] => {
 142	 const dates = [...new Set(attendanceData.map(record => record.date))];
 143	 return dates.sort();
 144	};
 145	
 146	// Get attendance summary by date
 147	export const getAttendanceSummaryByDate = (date: string) => {
 148	 const dayRecords = getAttendanceByDate(date);
 149	 const classSummaries = [];
 150	 
 151	 const classes = ['1A', '1B', '2A', '2B', '3A', '3B', '4A', '4B', '5A', '5B', '6A', '6B'];
 152	 
 153	 for (const className of classes) {
 154	 const classRecords = dayRecords.filter(r => r.className === className);
 155	 const stats = calculateAttendanceStats(classRecords);
 156	 
 157	 classSummaries.push({
 158	 className,
 159	 date,
 160	 ...stats
 161	 });
 162	 }
 163	 
 164	 return classSummaries;
 165	};
 166	
 167	// Get attendance summary by class for date range
 168	export const getAttendanceSummaryByClass = (className: string, startDate: string, endDate: string) => {
 169	 const classRecords = getAttendanceByClassAndDateRange(className, startDate, endDate);
 170	 const stats = calculateAttendanceStats(classRecords);
 171	 
 172	 return {
 173	 className,
 174	 startDate,
 175	 endDate,
 176	 ...stats
 177	 };
 178	};
