 57	 'Sinta Bella', 'Doni Saputra', 'Mega Wati', 'Rian Hidayat', 'Tari Sari',
 58	 'Vino Mahendra', 'Yuni Astuti', 'Zaki Maulana', 'Nisa Aulia', 'Oki Setia',
 59	 'Putri Ayu'
 60	 ];
 61	
 62	 data.metadata.classes.forEach(className => {
 63	 for (let i = 1; i <= 36; i++) {
 64	 const studentId = `${className.toLowerCase()}-${i.toString().padStart(3, '0')}`;
 65	 data.students.push({
 66	 id: studentId,
 67	 name: studentNames[i - 1] || `Siswa ${i}`,
 68	 className: className
 69	 });
 70	 }
 71	 });
 72	
 73	 // Generate attendance records
 74	 const startDate = new Date('2024-12-01');
 75	 const endDate = new Date('2025-01-31');
 76	 let currentDate = new Date(startDate);
 77	 let recordId = 1;
 78	 let schoolDays = 0;
 79	
 80	 while (currentDate <= endDate) {
 81	 // Skip weekends
 82	 if (currentDate.getDay() !== 0 && currentDate.getDay() !== 6) {
 83	 schoolDays++;
 84	 const dateString = currentDate.toISOString().split('T')[0];
 85	 
 86	 data.students.forEach(student => {
 87	 // Generate realistic attendance patterns
 88	 const randomFactor = Math.random();
 89	 let status, timeIn, notes;
 90	 
 91	 if (randomFactor < 0.85) {
 92	 status = 'Hadir';
 93	 const hour = 7;
 94	 const minute = Math.floor(Math.random() * 31);
 95	 timeIn = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
 96	 } else if (randomFactor < 0.93) {
 97	 status = 'Terlambat';
 98	 const hour = Math.random() < 0.7 ? 7 : 8;
 99	 const minute = hour === 7 ? Math.floor(Math.random() * 29) + 31 : Math.floor(Math.random() * 31);
 100	 timeIn = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
 101	 notes = 'Terlambat datang ke sekolah';
 102	 } else if (randomFactor < 0.98) {
 103	 status = 'Tidak Hadir';
 104	 notes = 'Tidak hadir tanpa keterangan';
 105	 } else {
 106	 status = 'Izin';
 107	 notes = data.excuseReasons[Math.floor(Math.random() * data.excuseReasons.length)];
 108	 }
 109	 
 110	 const record = {
 111	 id: `att-${recordId.toString().padStart(6, '0')}`,
 112	 studentId: student.id,
 113	 studentName: student.name,
 114	 className: student.className,
 115	 date: dateString,
 116	 status: status,
 117	 recordedBy: data.teachers[student.className],
 118	 recordedAt: `${dateString}T08:00:00.000Z`
 119	 };
 120	
 121	 if (timeIn) record.timeIn = timeIn;
 122	 if (notes) record.notes = notes;
 123	
 124	 data.attendanceRecords.push(record);
 125	 recordId++;
 126	 });
 127	 }
 128	 
 129	 currentDate.setDate(currentDate.getDate() + 1);
 130	 }
 131	
 132	 data.metadata.totalRecords = data.attendanceRecords.length;
 133	 data.metadata.schoolDays = schoolDays;
 134	
 135	 return data;
 136	};
 137	
 138	// Generate and save the data
 139	console.log('Generating attendance data...');
 140	const attendanceData = generateAttendanceJSON();
 141	
 142	console.log(`Generated ${attendanceData.metadata.totalRecords} attendance records`);
 143	console.log(`School days: ${attendanceData.metadata.schoolDays}`);
 144	console.log(`Students: ${attendanceData.students.length}`);
