 62	 return classMatch && statusMatch && searchMatch;
 63	 });
 64	
 65	 // Sort the filtered results
 66	 return filtered.sort((a, b) => {
 67	 // If showing all classes, sort by class first, then by name
 68	 if (filterClass === 'Semua') {
 69	 if (a.class !== b.class) {
 70	 return a.class.localeCompare(b.class);
 71	 }
 72	 }
 73	 // Then sort by student name
 74	 return a.name.localeCompare(b.name);
 75	 });
 76	 })();
