 228	 <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
 229	 Periode
 230	 </label>
 231	 <select
 232	 value={selectedDateRange}
 233	 onChange={(e) => setSelectedDateRange(e.target.value)}
 234	 className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white"
